$('input[name="period_type"]').on("change",function(){label=$('label[for="'+this.id+'"]');posters=$('div[data-period="'+this.value+'"]');if($(this).prop("checked")){label.addClass("active");posters.each(function(i,elm){$(elm).parent().show("fast");});}else{label.removeClass("active");posters.each(function(i,elm){$(elm).parent().hide("fast");});}});$("#info_btn").on("click",function(){$("#updateInfoModal").modal("show");updatePreview("#markdown_edit");});let updatePreview=function(elm){if(!$(elm).val()){return;};let html=marked($(elm).val());$("#update-info").html(html);};$(function(){$("#markdown_edit").keyup(function(){updatePreview(this);});updatePreview($("#markdwon_edit"));});$("#update-info-editor").hide();$("#editor-save-btn").hide();$("#editor-toggle-btn").on("click",function(){$("#update-info-editor").toggle();$("#editor-save-btn").toggle();});